import { IProduct } from '../components/product/Product';

export const PRODUCT: number = 10;

export interface IRouteNode {
    name: string;
    to: string;
}

export interface IRoute {
    main: IRouteNode;
    subs: IRouteNode[];
}

export const getCategoryRoutes = (): IRoute[] => {
    return [
        {
            main: { name: 'OUTER', to: 'outer' },
            subs: [
                { name: '코트', to: 'cot' },
                { name: '패딩', to: 'pad' },
                { name: '후드', to: 'hood' },
                { name: '가디건', to: 'gudi' },
                { name: 'MA - 1', to: 'ma' },
            ],
        },
        {
            main: { name: 'TOP', to: 'top' },
            subs: [
                { name: 'OUTER', to: 'outer' },
                { name: 'OUTER', to: 'outer' },
            ],
        },
        {
            main: { name: 'PANTS', to: 'pants' },
            subs: [
                { name: 'OUTER', to: 'outer' },
                { name: 'OUTER', to: 'outer' },
            ],
        },
        {
            main: { name: 'SHIRTS', to: 'shirts' },
            subs: [
                { name: 'OUTER', to: 'outer' },
                { name: 'OUTER', to: 'outer' },
            ],
        },
        {
            main: { name: 'SHOES', to: 'shoes' },
            subs: [
                { name: 'OUTER', to: 'outer' },
                { name: 'OUTER', to: 'outer' },
            ],
        },
    ];
};

export const getProducList = (key: string): IProduct[] => {

    const list = [
        {
            key: 'outer', items: [
                { id: 21, category: 'outer', oriPrice: 1000, salePrice: 500, name: 'outer1', tag: 'hot' } as IProduct,
                { id: 22, category: 'outer', oriPrice: 2000, salePrice: 0, name: 'outer2', tag: 'hot' } as IProduct,
                { id: 23, category: 'outer', oriPrice: 3000, salePrice: 0, name: 'outer3', tag: 'best' } as IProduct,
                { id: 24, category: 'outer', oriPrice: 4000, salePrice: 0, name: 'outer4', tag: 'best' } as IProduct,
                { id: 25, category: 'outer', oriPrice: 5000, salePrice: 0, name: 'outer5', tag: 'none' } as IProduct,
                { id: 26, category: 'outer', oriPrice: 6000, salePrice: 0, name: 'outer6', tag: 'none' } as IProduct,
                { id: 27, category: 'outer', oriPrice: 7000, salePrice: 0, name: 'outer7', tag: 'none' } as IProduct,
            ],
        },
        {
            key: 'top', items: [
                { id: 11, category: 'top', oriPrice: 1000, salePrice: 500, name: 'top1', tag: 'hot' } as IProduct,
                { id: 12, category: 'top', oriPrice: 2000, salePrice: 0, name: 'top2', tag: 'hot' } as IProduct,
                { id: 13, category: 'top', oriPrice: 3000, salePrice: 0, name: 'top3', tag: 'best' } as IProduct,
                { id: 14, category: 'top', oriPrice: 4000, salePrice: 0, name: 'top4', tag: 'best' } as IProduct,
                { id: 15, category: 'top', oriPrice: 5000, salePrice: 0, name: 'top5', tag: 'none' } as IProduct,
                { id: 16, category: 'top', oriPrice: 6000, salePrice: 0, name: 'top6', tag: 'none' } as IProduct,
                { id: 17, category: 'top', oriPrice: 7000, salePrice: 0, name: 'top7', tag: 'none' } as IProduct,
            ],
        },
    ];

    return list.filter(item => item.key === key).map(item => item.items)[0];
};
