import React from 'react';
import Product, { IProduct } from './Product';

interface Props {
    category: string;
    items: IProduct[];
}

export class ProductList extends React.Component<Props> {

    public render() {
        const { items } = this.props;
        const list = this.getProductList(items);
        return (
            <div className="pg-showcase__body">
                {list.map((item, index) => this.renderRow(item, index))}
            </div>
        );
    }

    private renderRow = (items: IProduct[], idx: number) => {
        return (
            <div key={idx} className="pg-showcase__body__row">
                {items && items.map((item, index) => <Product key={index} item={item} />)}
            </div>
        );
    }

    private renderProduct = (items: IProduct[], start: number, end: number) => {
        const list = items.splice(start, end);
        if (list.length <= 0) {
            return null;
        }
        return (list.map((item, index) => <Product key={index} item={item} />));
    }

    private getProductList = (items: IProduct[]): IProduct[][] => {
        const newList: IProduct[][] = [];
        const row = Math.floor(items.length / 4) + (items.length % 4 !== 0 ? 1 : 0);
        for (let i = 0; i < row; ++i) {
            const start = i * 4;
            const end = items.length < (i + 1) * 4 ? items.length : (i + 1) * 4;
            const list = items.splice(start, end);
            newList.push(list);
        }

        return newList;
    }
}
