import React from 'react';
import Product, { IProduct } from './Product';

interface Props {
    category: string;
    items: IProduct[];
}

export class ProductList extends React.Component<Props> {

    public render() {
        const { items } = this.props;
        const list: IProduct[][] = [];
        list.push(items.splice(0, 1));
        return (
            <div className="pg-showcase__body">
                {items && items.map((item, index) => <Product key={index} item={item} />)}
                {this.renderRow(items)}
            </div>
        );
    }

    private renderRow = (items: IProduct[]) => {
        const row = Math.floor(items.length / 4) + (items.length % 4 !== 0 ? 1 : 0);
        return (
            <div className="pg-showcase__body__row">
                {items && items.map((item, index) => <Product key={index} item={item} />)}
                {this.renderProduct(items)}
            </div>
        );
    }

    private renderProduct = (items: IProduct[], start: number, end: number) => {
        const list = items.splice(start, end);
        if (list.length <= 0) {
            return null;
        }
        return (list.map((item, index) => <Product key={index} item={item} />));
    }
}
