import React, { Component } from 'react';
import { RouteComponentProps } from 'react-router-dom';
import { getCategoryRoutes, getProducList } from '../../constants';
import { ProductList } from '../product/ProductList';

const list = getCategoryRoutes();

interface ShowcaeProps {
    category: string;
}

interface MatchParams {
    category: string;
}

type MatchProps = RouteComponentProps<MatchParams>;

type Props = ShowcaeProps & MatchProps;

interface State {

}

export class Showcase extends Component<Props, State> {


    public constructor(props: Props) {
        super(props);

    }

    public render() {
        const { match } = this.props;
        const category = this.getCategory(match.params.category);
        return (
            <>
                <p>{category}</p>
                <ProductList category={category} items={getProducList(category)} />
            </>
        );
    }

    private getCategory(path: string) {
        let category = 'none';
        for (let i = 0; i < list.length; ++i) {
            if (path.includes(list[i])) {
                category = list[i];
                break;
            }

        }
        return category;
    }
}
